import { Link, useNavigate } from "react-router-dom";
import logo from "../../images/logo.svg";
// import { onLogin } from "../../services/actions/user";
import Input from "./Input";
// import { isPassword, isEmail } from "../../utils/validation";

function Login() {

  return (
    <section className="auth">
      <Link to="/" className="auth__logo">
        <img src={logo} alt="Логотип" />
      </Link>
      <h2 className="auth__title">Рады видеть!</h2>
      <form className="auth__form" onSubmit={handleSubmit}>
        <div className="auth__input-container">
          <Input
            type="email"
            name="email"
            title="E-mail"
            onChange={handleChange}
            error={error.email}
            disabled={disabled}
          />
          <Input
            type="password"
            name="password"
            title="Пароль"
            onChange={handleChange}
            error={error.password}
            disabled={disabled}
          />
        </div>
        <span className="auth__message">{authMessage}</span>
        <button
          className={`${buttonProps.className} text`}
          disabled={disabled || buttonProps.disabled}
        >
          Войти
        </button>
        <div className="auth__link-container">
          <p className="text color_text">Ещё не зарегистрированы?</p>
          <Link to="/sign-up" className="auth__link text">
            Регистрация
          </Link>
        </div>
      </form>
    </section>
  );
}

export default Login;
