function MoviesCard({ movie }) {

  return (
    <article className="card color_background">
      <div className="card__header">
        <div>
          <h3 className="card__title text_subtitle">{movie.nameRU}</h3>
          <p className="card__duration text color_text">{}</p>
        </div>
        <button
          className={`card__favorite color_secondary link`}
        ></button>
      </div>
      <a
        href={movie.trailerLink}
        target="_blank"
        rel="noopener noreferrer"
        className="card__trailer-link"
      >
        <img className="card__image" src='#' alt={movie.nameRU} />
      </a>
    </article>
  );
}

export default MoviesCard;
